import { useState } from 'react'


const Header = (props) => {
  return (
      <h1>{props.course}</h1>
  )
}


const Total = (props) => {
  return (
      <p>
      it is the total component, Number of exercises {props.exercises1 + props.exercises2 + props.exercises3} 
      </p>
  )
}

const Part = (props) => {
  return (
  <p>{props.part} {props.exercises}</p>
  )
}


const Content = (props) => {

  return (
    <>
      <Part course={props.course} exercises={props.exercises} />
      {/* <Part part={part2} exercises={exercises2} /> */}
      {/* <Part part={part3} exercises={exercises3} /> */}
    </>
  )
}


const App = () => {
  const course = 'Half Stack application development'
  const part1 = {
    name: 'Fundamentals of React',
    exercises: 10
  }
  const part2 = {
    name: 'Using props to pass data',
    exercises: 7
  }
  const part3 = {
    name: 'State of a component',
    exercises: 14
  }

  return (
    <div>
      <Header course={course} />
      <Part course={part1.name} exercises={part1.exercises} />

    </div>
  )
}
